var MIN_LENGTH=2;$(document).ready(function(){$("#keyword").keyup(function(){var e=$("#keyword").val();e.length>=MIN_LENGTH?$.get("phpinc/auto-complete.php",{keyword:e}).done(function(e){$("#results").html("");var t=jQuery.parseJSON(e);$(t).each(function(e,t){$("#results").append('<div class="item">'+t+"</div>")}),$(".item").click(function(){var e=$(this).html();$("#keyword").val(e)})}):$("#results").html("")}),$("#keyword").blur(function(){$("#results").fadeOut(500)}).focus(function(){$("#results").show()})});